import styled from "styled-components/macro";

interface CardProps {
    isCardOpen: boolean
}

const ExpandableContainer = styled.div<CardProps>`
  min-height: 40px;
  height: 40px;
  width: 440px;
  border-radius: 4px;
  background: ${({isCardOpen}) => isCardOpen ? '#FF008C': '#FFF'};
`;

const ExpandableHeader = styled.p`
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
  font-weight: 500;
  font-size: 14px;
  line-height: 150%;
  padding: 8px;
`;

const SubCard = styled.div<CardProps>`
  min-height: 40px;
  height: 40px;
  padding: 8px
  border-radius: 4px;
  background: ${({isCardOpen}) => isCardOpen ? '#FF008C': '#FFF'};
`;

export {
    ExpandableContainer,
    ExpandableHeader
}
